import './../styles/css/main.css';
import React from 'react'
import { useForm } from 'react-hook-form';

export default function Form() {
  const { register, handleSubmit, setError, formState: { errors }, clearErrors } = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);
  
  return (
    <div className='form-contact'>
        <p>Déjanos tu mensaje y te contactaremos.</p>
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
                <label className="label">Nombre y Apellido:</label>
                <input type="text" placeholder="Nombre y Apellido" {...register("nameUser", {required: true, min: 3, maxLength: 80, pattern: /[A-Za-z]/i})} onClick={() => clearErrors()} />
                {errors.nameUser && <p>{errors.nameUser.message}</p>}
            </div>
            <div className="form-group">
                <label className="label">Empresa:</label>
                <input type="text" placeholder="Empresa" {...register("company", {required: true, min: 3, maxLength: 100})} onClick={() => clearErrors()} />
                {errors.company && <p>{errors.company.message}</p>}
            </div>
            <div className="form-group">
                <label className="label">Correo Electrónico:</label>
                <input type="text" placeholder="Email de Contacto:" {...register("emailContact", {required: true, pattern: /^\S+@\S+$/i})} onClick={() => clearErrors()} />
                {errors.emailContact && <p>{errors.emailContact.message}</p>}
            </div>
            <div className="form-group">
                <label className="label">Asunto:</label>
                <select {...register("subject", { required: true })}>
                    <option value="Servicio">Servicio</option>
                    <option value="Evento"> Evento</option>
                    <option value="Consulta"> Consulta</option>
                    <option value="Otros..."> Otros...</option>
                </select>
                {errors.subject && <p>{errors.subject.message}</p>}
            </div>
            <div className="form-group">
                <label className="label">Mensaje:</label>
                <textarea {...register("msgUser", {required: true, max: 10, maxLength: 450})} />
                {errors.msgUser && <p>{errors.msgUser.message}</p>}
            </div>
            <div className="form-group">
                {/* <input type="submit" value="Enviar" onClick={() => {
                    setError("nameUser", { type: "focus" }, { shouldFocus: true });
                }}/> */}

                <button
                    type="button"
                    onClick={() => {
                    [
                        {
                        type: "manual",
                        name: "nameUser",
                        message: "Por favor indícanos tu nombre!"
                        },
                        {
                        type: "manual",
                        name: "company",
                        message: "Queremos saber el nombre de tu empresa o donde trabajas!"
                        },
                        {
                        type: "manual",
                        name: "mailContact",
                        message: "Indícanos un correo para contactarte!"
                        },
                        {
                        type: "manual",
                        name: "subject",
                        message: "De que se trata tu consulta?"
                        },
                        {
                        type: "manual",
                        name: "msgUser",
                        message: "Cuéntanos acerca de tu consulta!"
                        }
                    ].forEach(({ name, type, message }) =>
                        setError(name, { type, message })
                    );
                    }}
                >
                    Enviar
                </button>
            </div>
                
        </form>
    </div>
  );
}
