// export default Mapa

// API google maps: AIzaSyDuj1zTOb2Tb8JDPZjgo9FbI7wnKbGQa70

import React, { Fragment } from "react";
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';
  const containerStyle = {
    width: '100%',
    height: '100%'
  };

  const center = {
    lat: 20.5937,
    lng: 78.9629
  };
  const Mapa = ()=>{
    const { isLoaded } = useJsApiLoader({
      id: 'google-map-script',
      googleMapsApiKey: "AIzaSyDuj1zTOb2Tb8JDPZjgo9FbI7wnKbGQa70"
  })

  const [map, setMap] = React.useState(null)
  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);
    setMap(map)
  }, [])
  const onUnmount = React.useCallback(function callback(map) {
    setMap(null)
  }, [])
  
  return (
    <div className='map-area'>
      {isLoaded ? <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={18} onLoad={onLoad} onUnmount={onUnmount}><></></GoogleMap> : <></>}
    </div>
  )  
}

export default Mapa;
